$(function(){

    $("#productDiv").on("keyup","textarea",function(){var a=$(this);a.val().length>500?a.val(a.val().substring(0,500)):a.siblings(".word_count").children("span").text(500-a.val().length)}),

        $("#productForm").on("mouseenter",".new_product input",function(){$(".product_upload div").css("backgroundColor","#7e9597")}),

        $("#productForm").on("mouseleave",".new_product input",function(){$(".product_upload div").css("backgroundColor","#93b7bb")}),

        $("#productForm").validate({
            onkeyup:!1,
            focusCleanup:!0,
            rules:{
                "productInfos[0].product":{required:!1,minlength:2,maxlenStr:20},
                "productInfos[1].productUrl":{required:!1,digits:!0,maxlength:120},
                "productInfos[2].productProfile":{required:!1,specialchar:!0,checkNum:!0,minlength:10,maxlength:500}
            },
            submitHandler:function(a){
                var b=$(a).find('input[type="text"]'),
                    c=$(a).find("textarea");
                b.each(function(){$(this).val()==$(this).attr("placeholder")&&$(this).val("")}),
                    c.each(function(){$(this).text()==$(this).attr("placeholder")&&$(this).text("")}),
                    $(a).find(":submit").attr("disabled",!0),
                    a.submit()
            }
        })
});