$(function(){

    $("#memberForm").validate({
        onkeyup:!1,
        focusCleanup:!0,
        rules:{
            "leaderInfos[0].name":{
                required:!1,
                specialchar:!0,
                checkNum:!0,
                minlength:2,
                maxlenStr:20
            },
            "leaderInfos[1].position":{
                required:!1,
                specialchar:!0,
                checkNum:!0,
                maxlenStr:60
            },
            "leaderInfos[2].weibo":{
                required:!1,
                digits:!0,
                maxlength:11,
                minlength:11
            }
        },
        messages:{
            "leaderInfos[0].name":{specialchar:"请输入真实的创始人姓名",checkNum:"请输入真实的创始人姓名",minlength:"请输入真实的创始人姓名",maxlenStr:"请输入真实的创始人姓名"},
            "leaderInfos[1].position":{specialchar:"请输入真实的当前职位",checkNum:"请输入真实的当前职位",maxlenStr:"请输入真实的当前职位"},
            "leaderInfos[2].weibo":{digits:"请输入正确的手机号",maxlength:"请输入正确的手机号",minlength:"请输入正确的手机号"},

        },
        submitHandler:function(a){
            var b=$(a).find('input[type="text"]'),
                c=$(a).find("textarea");
                b.each(function(){$(this).val()==$(this).attr("placeholder")&&$(this).val("")}),
                c.each(function(){$(this).text()==$(this).attr("placeholder")&&$(this).text("")}),
                $(a).find(":submit").attr("disabled",!0),
                a.submit()
        }
    })
});